@use "../base" as *;
@use "../abstracts" as *;
@use "../globals/" as *;
@use "../components" as *;

// Required Core Stylesheet
@import "node_modules/@glidejs/glide/src/assets/sass/glide.core";
// Optional Theme Stylesheet
@import "node_modules/@glidejs/glide/src/assets/sass/glide.theme";

.sign-up__contx {
  background: map-get($yellow-variant, placebo-yellow);
  @include container-small;

  @include breakpoint {
    @include container-bg_sign-up;
    display: flex;
    justify-content: space-between;
  }

  .left {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;

    @include breakpoint {
      padding: 2rem 4rem 4rem;
      width: 50%;
    }

    .logo {
      display: flex;
      justify-content: space-between;
      align-items: center;

      .logo-text {
        font-weight: map-get($font-weights, bold);
        font-size: map-get($type-scale-small, 300);

        @include breakpoint {
          font-size: map-get($type-scale-small, 400);
          cursor: pointer;
        }
      }

      .go-back {
        display: flex;
        justify-content: center;
        align-items: center;
        gap: 0.15rem;
        font-size: map-get($type-scale-small, 200);
        padding: 0.5rem;
        border: 1px solid map-get($gray-variant, silver-back);
        border-radius: 3px;

        @include breakpoint {
          font-size: map-get($type-scale-big, 100);
          cursor: pointer;
        }

        .ph-arrow-left {
          font-weight: map-get($font-weights, bold);
        }

        p {
          font-weight: map-get($font-weights, semi-bold);
        }
      }
    }

    .create-account {
      font-size: map-get($type-scale-small, 800);
      font-weight: map-get($font-weights, bold);
      max-width: 24rem;

      @include breakpoint {
        font-size: map-get($type-scale-big, 400);
        max-width: 35rem;
      }
    }

    form {
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      gap: 1.5rem;

      div > label.input_ {
        display: block;
        font-weight: map-get($font-weights, semi-bold);
        font-size: map-get($type-scale-small, 300);
        margin-bottom: 0.45rem;
      }

      div input.input-area {
        font-size: map-get($type-scale-small, 300);
        background: inherit;
        width: 90%;
        min-width: 14rem;
        max-width: 23rem;
        padding: 0.95rem 0.75rem;
        border: 1px solid map-get($gray-variant, silver-back);
        border-radius: 5px;
        outline: none;

        &:focus {
          background: map-get($white-variant, white_);
        }

        @include breakpoint {
          max-width: 30rem;

          &:focus {
            background: map-get($white-variant, doctor);
          }
        }
      }

      .password-n-eye {
        display: flex;
        justify-content: flex-start;
        align-items: center;
        position: relative;
        width: 90%;
        min-width: 14rem;
        max-width: 23rem;

        @include breakpoint {
          max-width: 30rem;
        }

        input.do {
          width: 100%;
        }

        .eye {
          position: absolute;
          right: 1rem;

          &.click {
            filter: invert(80%);
          }

          @include breakpoint {
            cursor: pointer;

            &:hover,
            &:focus {
              filter: invert(50%);
            }
          }
        }
      }

      .terms_conditions {
        max-width: 23rem;
        line-height: 1.7rem;

        @include breakpoint {
          max-width: 35rem;
        }

        label.for-agreement {
          a {
            color: map-get($blue-variant, royalty);
            font-weight: map-get($font-weights, bold);
          }
        }
      }
    }

    .already-hv__acct {
      width: 90%;
      text-align: center;
      min-width: 14rem;
      max-width: 22rem;

      @include breakpoint {
        text-align: center;
        max-width: 30rem;
      }

      a {
        text-decoration: none;
        color: map-get($blue-variant, royalty);
        font-weight: map-get($font-weights, regular);

        &:hover {
          text-decoration: underline;
        }
      }
    }
  }

  .right {
    display: none;

    @include breakpoint {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      gap: 1.5rem;
      background: map-get($blue-variant, royalty);
      width: 50%;

      .glide {
        &__track {
          .glide__slides {
            width: 70%;
            margin: 0 auto;
            text-align: center;

            .glide__slide {
              .right-img {
                width: 80%;
                margin: 0 auto;
                border-radius: 5px 5px 0 0;

                img {
                  width: 100%;
                  height: 100%;
                  object-fit: cover;
                  border-radius: 5px 5px 0 0;
                }
              }

              .right-text {
                text-align: center;
                width: 70%;
                max-width: 32rem;
                margin: 0 auto;

                .right-text__header {
                  font-size: map-get($type-scale-big, 400);
                  font-weight: map-get($font-weights, semi-bold);
                  color: map-get($white-variant, doctor);
                }

                .right-text__description {
                  font-size: map-get($type-scale-big, 100);
                  font-weight: map-get($font-weights, light);
                  color: map-get($white-variant, doctor);
                }
              }
            }
          }
        }
      }
    }

    .glide__bullets {
      cursor: pointer;
    }
  }
}
